import { useSelector, useDispatch } from 'react-redux';
import { selectTodos } from '../features/todos/todosSelectors';
import { removeTodo, toggleTodo } from '../features/todos/todosSlice';

const TodoList = () => {
  const todos = useSelector(selectTodos);
  const dispatch = useDispatch();

  return (
    <ul>
      {todos.map(todo => (
        <li key={todo.id}>
          <span onClick={() => dispatch(toggleTodo(todo.id))} style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>
            {todo.text}
          </span>
          <button onClick={() => dispatch(removeTodo(todo.id))}>Удалить</button>
        </li>
      ))}
    </ul>
  );
};

export default TodoList;
